import NavBarCharity from './NavBarCharity';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
import background from '../charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from '../Footer';



class HomeCharity extends React.Component {




    // get user details from session storage
    componentDidMount() {
        this.userData = JSON.parse(sessionStorage.getItem('user'));
        console.log(this.userData)

        if (sessionStorage.getItem('user')) {
            this.setState({
                userid: this.userData.userid,
                username: this.userData.username,
                usertype: this.userData.usertype,
                loggedin: true
            })
        }else {
            this.setState({
                userid: '',
                username: '',
                usertype: '',
                loggedin: false
            })
        }

    }

        constructor(props) {
            super(props);
            this.state = {
                currentPage: "HomePageCharity"
            };
          }

          

    render() {
        const { usertype } = this.state;
        console.log(usertype)
        
        
        if (usertype==2) {
            return (
                <>
                    <NavBarCharity data={this.state}/>
                    <h1>Welcome {this.state.username} you have been logged in as a Charity </h1>
                    <Footer />
                </>
            )
        }
        return <p><Redirect to="/login" /></p>;
        
    }
}

ReactDOM.render(
    <HomeCharity />,
    document.getElementById('root')
);


export default HomeCharity;
