import TopNavBar from '../TopNavBar';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
//import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from '../Footer';
import Home from '../Home';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
//import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

const classes = useStyles();


//let username = this.state.username;

const [username, setUserName] = React.useState(this.state.username);
const [email, setEmail] = React.useState(this.state.email);
const [password, setPassword] = React.useState(this.state.password);

const handleUserNameChange = event => setUserName(event.target.value);
const handlEmailChange = event => setEmail(event.target.value);
const handlePasswordChange = event => setPassword(event.target.value);

async function user(userLogin) {
    const response = await fetch("/api/user/byusernamepassword", {
        method: "GET", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(userLogin) // body data type must match "Content-Type" header
    });
    let users =  response.json();

}


const handleSubmit = variables => {
    const userLogin = { username, email, password};
    user(userLogin);
    //details1(detailsInput);
    setUserName(response.username);
    setEmail(response.email);
    setPassword(respopnse.password);
};

class Logon extends React.Component {

    constructor(props) {
        super(props);

      this.state = {
        currentPage: "Login",
        username: '',
        password: '',
        email: "",
        hasLoginFailed: false,
        showSuccessMessage: false
    }


    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
}

handleChange(event) {
    this.setState(
        {
            [event.target.name]
                : event.target.value
        }
    )
}

loginClicked() {
    if(this.state.username==='in28minutes' && this.state.password==='dummy'){
        //AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
        this.setState({showSuccessMessage:true})
        this.setState({hasLoginFailed:false})
    }
    else {
         this.setState({showSuccessMessage:false})
         this.setState({hasLoginFailed:true})
    }
}



    render() {
        return (
            <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <GroupIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Login
        </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="username"
                                value={username}
                                label="UserName"
                                userName="userName"
                                autoComplete="username"
                                onChange={handleUserNameChange}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                autoComplete="email"
                                name="email"
                                variant="outlined"
                                required
                                fullWidth
                                value={email}
                                id="email"
                                label="Email"
                                onChange={handlEmailChange}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="password"
                                value={password}
                                label="Password"
                                name="password"
                                autoComplete="password"
                                onChange={handlePasswordChange}
                            />
                        </Grid>
                    </Grid>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        preventDefault
                        className={classes.submit}
                        onClick={handleSubmit}>
                        Register
                    </Button>

                    <Grid container justify="center">
            <Grid item>
              <Nav.Link to="/view">View Employee Records</Nav.Link>
            </Grid>
          </Grid>
                </form>
            </div>
            <div>
                <h1>Login</h1>
                <div className="container">
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
            <Form>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
        </Container >
        )
    }
}


class Login extends React.Component {

    
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Login"
        };
      }




    render() {
        return (
            <>
                <TopNavBar data={this.state}/>
                <Logon data={this.state}/>
                
                <Footer />
            </>
        )
    }
}

export default Login;
