import NavBarAdmin from './NavBarAdmin';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
import background from '../charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import Footer from '../Footer';
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap'

const useStyles = makeStyles(theme => ({
    table: {
      minWidth: 600
    },
    avatar: {
      margin: theme.spacing(1),
      backgroundColor: theme.palette.secondary.main
    },
    paper: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      margin: `10px`,
      height: "100%",
      width: "99%",
      marginTop: theme.spacing(7)
    },
    link: {
      color: "rgba(0,0,0,0.65)",
      textDecoration: "none",
      marginLeft: "10%",
      alignSelf: "flex-start",
      "&:hover": {
        color: "rgba(0,0,0,1)"
      }
    }
  }));

function PendingDonors() {
    const classes = useStyles();
  
    const [donators, upDateDonators] = React.useState([]);
    const [firstLoad, setLoad] = React.useState(true);
    const [reviewuserid, setReview] = React.useState([]);
    let isLoading = true;
  
    async function donorList() {
      let response = await fetch("/api/donators/");
      let donators = await response.json();
      upDateDonators(donators);   
    }

    async function completeRequest(toInput) {
        const response = await fetch("/api/user/update", {
            method: "PUT", // *GET, POST, PUT, DELETE, etc.
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json"
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
