import TopNavBar from './TopNavBar';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
//import { Route, BrowserRouter as Router } from "react-router-dom";
import { BrowserRouter, Router, Route, Switch, Redirect } from 'react-router-dom';
//import Table from "./Components/Table";
//import App from './App';
//import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "./Components/Recipients";
import HomePageCharities from './Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from './Footer';
import Home from './Home';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
//import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
//import Logon from "./Components/Logon";
import HomeAdmin from './Components/HomeAdmin';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

async function failedLogon(userId) {


    const response = await fetch("api/user/failedlogon", {
        method: "PUT", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(userId) // body data type must match "Content-Type" header
    });
}


class Logon extends React.Component {

    constructor(props) {
        super(props);

      this.state = {
        currentPage: "Login",
        username: '',
        password: '',
        email: "",
        isLoading: true,
        hasLoginFailed: false,
        showSuccessMessage: false,
        users: [],
        userid: null,
        usertype: null,
        regapproval: null

    }


    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
}



handleChange(event) {
    this.setState(
        {
            [event.target.name]
                : event.target.value
        }
    )
}

loginClicked() {
  
  const { users, isLoading } = this.state;
  console.log(users);
  /*
  {users.map(users =>
    {(this.state.username===users.username||this.state.username===users.email) && this.state.password==users.password?
      this.setState({
        hasLoginFailed:false, 
        showSuccessMessage:true,
        username: (users.username),
        userid: (users.pk_user_id),
        usertype: (users.user_type),
        regapproval: users.reg_approval,
        failcount: users.fail_count
      }):    
      this.setState({hasLoginFailed:true})
    } 
    )}
    */
   {users.map(users => {
    {((this.state.username===users.username||this.state.username===users.email) && this.state.password==users.password) && 
        this.setState({
            hasLoginFailed:false, 
            showSuccessMessage:true,
            username: (users.username),
            userid: (users.pk_user_id),
            usertype: (users.user_type),
            regapproval: users.reg_approval,
            failcount: users.fail_count
          })}  
          {((this.state.username===users.username||this.state.username===users.email) && this.state.password!==users.password) && 
            this.setState({
                hasLoginFailed:true, 
                failedId: (users.pk_user_id),
                username: (users.username)
            })
            console.log(this.state)
            let userId = {
                pk_user_id: 4
            };
            failedLogon(userId);
        }  
   })}

   {users.map(users => {
          {((this.state.username===users.username||this.state.username===users.email) && this.state.password!==users.password) && 
            this.setState({
                hasLoginFailed:true, 
                failedId: (users.pk_user_id),
                username: (users.username)
            })
            console.log(this.state)
            let userId = {
                pk_user_id: this.state.failedId
            };
            failedLogon(userId);
        }  
   })}
}


// set user details to session storage
componentWillUpdate(nextProps, nextState) {
    localStorage.setItem('user', JSON.stringify(nextState));
}


//get userlist
async componentDidMount() {

  const response = await fetch('/api/user/');
  const body = await response.json();
  this.setState({ users: body, isLoading: false });
}




    render() {

      const { users, isLoading } = this.state;

      if (isLoading) {
          return <p>Loading...</p>;
      }
        return (
//                    {this.state.showSuccessMessage && <div>Login Sucessful for {this.state.username}, user id is {this.state.userid}, user type is {this.state.usertype}</div>}

            <div>                         
                <h1>Login</h1>
                <div className="container">
                    <form onSubmit={this.loginClicked}>
                    <div className="form-group">
                        <label>Username or Email</label>
                        <input type="text" name="username"  className="form-control" value={this.state.username} onChange={this.handleChange} />
                    </div>
                    <div className="form-group">
                        <label>Password</label>
                        <input type="password" name="password" className="form-control" value={this.state.password} onChange={this.handleChange} />
                    </div>
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials + {this.state.failedId}</div>}
                    {(this.state.showSuccessMessage && this.state.failcount>=3) && <div className="alert alert-warning">Locked id</div>}
                    {(this.state.showSuccessMessage && this.state.usertype==0 && this.state.failcount<4) && <Redirect to="/homeadmin"   />}
                    {(this.state.showSuccessMessage && this.state.usertype==1 && this.state.failcount<4) && <Redirect to="/homedonor"   />}
                    {(this.state.showSuccessMessage && this.state.usertype==2 && this.state.failcount<4) && <Redirect to="/homecharity" />}
                    <button type="submit" className="btn btn-primary btn-block">Login</button>
                </form>
                </div>
            </div>

            /*
                                User Name or Email: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.showSuccessMessage && <Redirect  to="/homeadmin"   />}
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                    */
            
        )
    }
}



class Login extends React.Component {

    
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Login"
        };
      }




    render() {
        return (
            <>
                <TopNavBar data={this.state}/>
                <Logon data={this.state}/>
                <Footer />
                
            </>
        )
    }
}

ReactDOM.render(
    <Login />,
    document.getElementById('root')
);

export default Login;
