import NavBarDonor from './NavBarDonor';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
import background from '../charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container, Nav } from 'react-bootstrap';
import Footer from '../Footer';



class HomeDonor extends React.Component {




    // get user details from session storage
    componentDidMount() {
        this.userData = JSON.parse(sessionStorage.getItem('user'));
        console.log(this.userData)

        if (sessionStorage.getItem('user')) {
            this.setState({
                userid: this.userData.userid,
                username: this.userData.username,
                usertype: this.userData.usertype,
                regapproval: this.userData.regapproval,
                loggedin: true
            })
        }else {
            this.setState({
                userid: '',
                username: '',
                usertype: '',
                loggedin: false
            })
        }

    }

        constructor(props) {
            super(props);
            this.state = {
                currentPage: "HomePageDonor"
            };
          }

          

    render() {
        const { usertype } = this.state;
        console.log(usertype)
        
        
        if (usertype==1) {
            return (
                <>
                    <NavBarDonor data={this.state}/>
                    <h1>Welcome {this.state.username} you have been logged in as a Donor, your application is {this.state.regapproval}</h1>
                    <Footer />
                </>
            )
        }
        return (
            <div>        
                <div>                     
            <h2>Users must be logged in with appropriate user type to view this page </h2>
            </div>
            <div>                     
            <Nav.Link href="/login">Please login at this link</Nav.Link>
           </div>
           </div>
        )
        
    }
}

ReactDOM.render(
    <HomeDonor />,
    document.getElementById('root')
);


export default HomeDonor;
