import React, { Component } from "react";
import ReactDOM from 'react-dom';
import logo from '../acons3.png';
import Details from './Details';
import background from '../charitybackground.jfif';
import '../Components/homepagecharities.css';

class HomePageCharities extends React.Component {


    state = {
        isLoading: true,
        groups: [],
        photoPath: ""
    };

    async componentDidMount() {
        /*const response = await fetch('/api/recipients/');
        const body = await response.json();
        const response1 = await fetch('/api/details/');
        const body1 = await response1.json();
        const response2 = await fetch('/api/name/');
        const body2 = await response2.json();
        this.setState({ recipients: body, details: body1, names: body2, isLoading: false }); */

        response = await fetch('/api/charities/');
        const body = await response.json();
        this.setState({ charities: body, isLoading: false});

    }



    render() {
        const { charities, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        return (
            /*
                <div class="Container">
                    <h2>Charity List</h2>
                    <div class="row">
                        {recipients.map(recipients =>
                            <div key={recipients.pk_recipient_id}> {details.map(details =>
                                <div key={details.pk_details_id}> {names.map(names =>
                                    <div key={names.pk_name_id}>
                                        <td>{details.fk_user_id === recipients.fk_user_id && names.fk_user_id === recipients.fk_user_id ?
                                            <div class="col-md-4" >
                                                <h2>{names.orgname}</h2>
                                                <a class="nav-item nav-link"  href={details.user_url}><img src={process.env.PUBLIC_URL + '/pictures/' + details.user_photo}
                                                     width="500px" height="500px" /></a>
                                                <a class="btn btn-secondary btn-sm" href="#" role="button">View details &raquo;</a>
                                            </div>
                                            : null}</td>
                                    </div>
                                )}
                                </div>
                            )}
                            </div>
                        )}
                    </div>
                </div>
                */

                <div class="Container">
                    <h2>Charity List</h2>
                </div>
        );

    }
}

export default HomePageCharities;
