import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import { Link } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap'

const useStyles = makeStyles(theme => ({
  table: {
    minWidth: 600
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },
  paper: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    margin: `10px`,
    height: "100%",
    width: "99%",
    marginTop: theme.spacing(7)
  },
  link: {
    color: "rgba(0,0,0,0.65)",
    textDecoration: "none",
    marginLeft: "10%",
    alignSelf: "flex-start",
    "&:hover": {
      color: "rgba(0,0,0,1)"
    }
  }
}));



class Logon extends React.Component {

    constructor(props) {
        super(props);

      this.state = {
        currentPage: "Login",
        username: '',
        password: '',
        email: "",
        isLoading: true,
        hasLoginFailed: false,
        showSuccessMessage: false,
        users: []
    }


    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
}



handleChange(event) {
    this.setState(
        {
            [event.target.name]
                : event.target.value
        }
    )
}

loginClicked() {
  
  const { users, isLoading } = this.state;
  console.log(users);
  {users.map(users =>
    //console.log(users.username)
    {if(this.state.username===users.username && this.state.password==users.password){
      //AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
      this.setState({showSuccessMessage:true})
      this.setState({hasLoginFailed:false})
  }
  else {
       this.setState({showSuccessMessage:false})
       this.setState({hasLoginFailed:true})
  }}
    )}

    /*
         {this.state.username === "anto" && this.state.password === "1"?
                    this.setState({hasLoginFailed:false}) :
                    this.setState({hasLoginFailed:true})} 
    
)}
*/
    /*
    if(this.state.username==="xxx" && this.state.password==="xxx"){
        //AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
        this.setState({showSuccessMessage:true})
        this.setState({hasLoginFailed:false})
    }
    else {
         this.setState({showSuccessMessage:false})
         this.setState({hasLoginFailed:true})
    }
    */
}

async componentDidMount() {

  const response = await fetch('/api/user/');
  const body = await response.json();
  this.setState({ users: body, isLoading: false });
}



    render() {

      const { users, isLoading } = this.state;

      if (isLoading) {
          return <p>Loading...</p>;
      }
        return (

            <div>
                <h1>Login</h1>
                <div className="container">
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
                <div class="Container">
                <h2>User List</h2>
                <div class="row">
                    {users.map(users =>
                        <div key={users.pk_user_id}> 
                            <td> {this.state.username === users.username && this.state.password === users.password?
                                <div class="col-md-4" >
                                    <h2>{users.pk_user_id}</h2>
                                </div> :
                                null}
                            </td> 
                        </div>
                    )}
                </div>
            </div>
            </div>
            
        )
    }
}

export default Logon;
