import TopNavBar from '../TopNavBar';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router, Redirect } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
//import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from '../Footer';
import Home from '../Home';
import Moment from 'moment';
import { format } from 'date-fns';

//import Registration from "../Components/Registration";

//import React, { useState } from 'react';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
//import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import CircularProgress from "@material-ui/core/CircularProgress";

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

const validEmailRegex =
    RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);

const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach(
        // if we have an error string set valid to false
        (val) => val.length > 0 && (valid = false)
    );
    return valid;
}

async function nameUpdate(toInput) {


    const response = await fetch("/api/user/insert", {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(toInput) // body data type must match "Content-Type" header
    });
    let newuser = await response.json();
    console.log(newuser.pk_user_id);
    let userid = newuser.pk_user_id;
    return userid;
}

async function detailsAdd(toInput) {
    const response = await fetch("/api/details/insert", {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(toInput) // body data type must match "Content-Type" header
    });
    let newdetails = await response.json();
    let newdetailsid = newdetails.pk_details_id;
    return newdetailsid;
}


class EditUserComponent extends Component {


    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            email: '',
            message: null,
            userid: this.props.userid,
            userstatement: null,
            user_type: null,
            user_added: 0,
            errors: {
                username: '',
                email: '',
                password: '',
                user_type: ''
            }
        }
        this.saveUser = this.saveUser.bind(this);
    }

    /*
    setStateAsync(state) {
        return new Promise((resolve) => {
            this.setState(state, resolve)
        });
    }
    */

    //get user
    async componentDidMount() {
        const response = await fetch('/api/user/');
        const body = await response.json();
        this.setState({ users: body, isLoading: false });
    }

    //get name
    async componentDidMount() {
        const response = await fetch('api/name/byuserid');
        const body = await response.json();
        this.setState({ name: body, isLoading: false });
    }

    validateUser = (e) => {
        e.preventDefault();
        if (validateForm(this.state.errors)) {
            console.log('Valid Form')
        } else {
            console.log('Invalid Input')
        }
    }

    saveName = (e) => {
        e.preventDefault();
        if (validateForm(this.state.errors)) {
            let name = {
                fk_user_id: this.state.userid, orgname: this.state.orgname
            };
            userAdd(user).then(res => {
                console.log(res)
                console.log(this.state.userid)

            });
        } else {
            console.log('Invalid Input')
        }


    }

    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });

    // set user details to session storage
    componentWillUpdate(nextProps, nextState) {
        sessionStorage.setItem('user', JSON.stringify(nextState));
    }

    handleChange = (e) => {
        e.preventDefault();
        const { name, value } = e.target;
        console.log(name + value)
        let errors = this.state.errors;
        const { users, isLoading } = this.state;

        switch (name) {
            case 'username':
                errors.username = value.length < 5 ? 'Username must be at least 5 characters long!' : '';
                break;
            case 'email':
                errors.email =
                    validEmailRegex.test(value)
                        ? ''
                        : 'Email must be valid!';
                break;
            case 'password':
                errors.password =
                    value.length < 8
                        ? 'Password must be at least 8 characters long!'
                        : '';
                break;
            case 'user_type':
                errors.user_type =
                    value.length === 0
                        ? 'Please select a user type'
                        : '';
                break;
            default:
                break;
        }

        if (name === 'username') {
            {
                users.map(users => { (value === users.username) && (errors.username = 'Username already in use, please choose another') }
                )
            }
        }
        if (name === 'email') {
            {
                users.map(users => { (value === users.email) && (errors.email = 'Email already in use, please choose another') }
                )
            }
        }
        this.setState({ errors, [e.target.name]: e.target.value }, () => {
            console.log(errors)
            console.log(errors.username)
        })

    }



    render() {

        return (
            <div className="container">
                <h2 className="text-center">Update Charity Details</h2>
                <form>
                    <div className="form-group">
                        <label>Charity Name:</label>
                        <input type="text" placeholder={this.state.orgname} name="orgname" className="form-control" value={this.state.orgname} onChange={this.handleChange} />
                    </div>
                    {!this.state.user_added == 1 ?
                        <div>
                            <button className="btn btn-primary btn-success btn-block" onClick={this.saveName}>Update Name</button>
                            <h2>{this.state.errors.username}</h2>
                            <h2>{this.state.errors.email}</h2>
                            <h2>{this.state.errors.password}</h2>
                        </div> :
                        <div><h2>Registration has been received</h2>
                            <Nav.Link href="/login"><h3>Please login</h3></Nav.Link>
                        </div>}
                </form>
            </div>
        );
    }
}



class EditDetailsCharity extends React.Component {


    // get user details from session storage
    componentDidMount() {
        this.userData = JSON.parse(localStorage.getItem('user'));
        console.log(this.userData)

        if (localStorage.getItem('user')) {
            this.setState({
                userid: this.userData.userid,
                username: this.userData.username,
                usertype: this.userData.usertype,
                loggedin: true
            })
        } else {
            this.setState({
                userid: '',
                username: '',
                usertype: '',
                loggedin: false
            })
        }

        if (this.state.usertype == '2') {
            this.setState({ loggedin: true })
        }
        else {
            console.log(this.state.usertype)
        }
    }




    constructor(props) {
        super(props);
        this.state = {
            currentPage: "CharityEditDetails"
        };
    }

    render() {
        return (
            <>
                <TopNavBar data={this.state} />
                <EditUserComponent data={this.state} />
                <Footer />
            </>
        )
    }
}

ReactDOM.render(
    <EditDetailsCharity />,
    document.getElementById('root')
);

export default EditDetailsCharity;
