                        <label>Charity Photo: </label>
                        <input type="file" placeholder={this.state.user_photo} name="file" className="form-control" value={this.state.user_photo} onChange={this.handleFileChange} />
                        <button type="button" class="btn btn-success btn-block" onClick={this.savePhoto}>Upload</button>
                    </div>
                </form>
                <br></br>
            </div>
        );
    }
}



