import TopNavBar from './TopNavBar';
import React, { Component } from "react";
import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "./Components/Recipients";

class Main extends React.Component {
    render() {
        return (
            <div class="page-header" >
                <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.5.3/dist/css/bootstrap.min.css" integrity="sha384-TX8t27EcRE3e/ihU7zmQxVncDAy5uIKz4rEkgIXeMed4M0jlfIDPvg6uqKI2xXr2" crossorigin="anonymous"></link>
                <div class="jumbotron" style={{ backgroundImage: `url(${background})` }}>
                    <div class="container">
                        <h2 class="display-3">
                            <p class="font-weight-bold">Helping others through Life</p>
                            <p class="font-weight-bold">Featuring the below Charities</p>
                        </h2>
                    </div>
                </div>
            </div>
        );
    }
}

class Charities extends React.Component {
    render() {

        const { recipients, details, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        return (
          /*  <div class="container">
                <div class="row">
                    <div class="col-md-4">
                        <h2>Oxfam</h2>
                        <a class="nav-item nav-link" href="https://donate.oxfamireland.org/euro/~my-donation?utm_source=brand&utm_medium=grants&utm_campaign=roi&ni=&gclid=EAIaIQobChMI29Sr7e_X7QIVC4BQBh10XgPeEAAYASAAEgLvQ_D_BwE"><img src={require('./pictures/oxfam.jfif')}
                            class="rounded float-left  aristphoto" width="100%" height="100%" /></a>
                        <a class="btn btn-secondary" href="#" role="button">View details &raquo;</a>
                    </div>
                    <div class="col-md-4">
                        <h2>Trocaire</h2>
                        <a class="nav-item nav-link"
                            href="https://www.trocaire.org/"><img
                                src={require('./pictures/trocaire.png')} class="rounded float-left aristphoto" width="100%" height="100%" /></a>
                        <a class="btn btn-secondary" href="#" role="button">View details &raquo;</a>
                    </div>
                    <div class="col-md-4">
                        <h2>PIPS</h2>
                        <a class="nav-item nav-link" href="https://pipscharity.com/"><img src={require('./pictures/pips.png')}
                            class="rounded float-left aristphoto" width="100%" height="100%" /></a>
                        <a class="btn btn-secondary" href="#" role="button">View details &raquo;</a>
                    </div>
                </div>
            </div> */
                        <div className="App">
                        <header className="App-header">
                            <div className="App-intro">
                                <h2>Charity List</h2>
                                {recipients.map(recipients =>
                                    <div key={recipients.pk_recipient_id}> {details.map(details =>
                                        <div key={details.pk_details_id}>
                                            <td>{details.fk_user_id===recipients.fk_user_id? details.fk_user_id: null}</td>
                                        </div> 
                                    )}
                                    </div> 
                                )}
                            </div>
                        </header>
                    </div>
        )
    }
}

class Home extends React.Component {
    render() {
        return (
            <>
                <TopNavBar />
                <Main />
                <Charities />
                <Recipients />
            </>
        )
    }
}

ReactDOM.render(
    <Home />,
    document.getElementById('root')
);

export default Home;
