import NavBarCharity from './NavBarCharity';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router, Redirect } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
//import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from '../Footer';
import Home from '../Home';
import Moment from 'moment';
import { format } from 'date-fns';
import axios from 'axios';


//import Registration from "../Components/Registration";

//import React, { useState } from 'react';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
//import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import CircularProgress from "@material-ui/core/CircularProgress";



const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

const validEmailRegex =
    RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);

const validateForm = (errors) => {
    let valid = true;
    Object.values(errors).forEach(
        // if we have an error string set valid to false
        (val) => val.length > 0 && (valid = false)
    );
    return valid;
}


async function projectAdd(toInput) {


    const response = await fetch("/api/projects/insert", {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(toInput) // body data type must match "Content-Type" header
    });
    //let existingname = await response.json();
    //let nameid = existingname.pk_name_id;
    //return addressid;
}

class ExistingProjects extends React.Component {


    state = {
        isLoading: true,
        groups: [],
        photoPath: ""
    };

    async componentDidMount() {
        const response = await fetch('/api/charities/');
        const body = await response.json();
        this.setState({ charities: body, isLoading: false });

    }

        //get recipientid
        async componentDidMount() {
            const responseDetails = await fetch('/api/recipients/pathbyuserid/' + this.state.userid);
            const responseRecipient = await responseDetails.json();
            this.setState({ recipient: responseRecipient, isLoading: false });
            console.log(responseRecipient)
        }


    render() {
        const { charities, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        return (


            <div class="Container">
                <h2>Charity List</h2>
                <div class="row">
                    {charities.map(charities =>
                        <div key={charities.pk_user_id}>
                            <td>
                                <div class="col-md-4" >
                                    <h2>{charities.orgname}</h2>
                                    <a class="nav-item nav-link" href={charities.user_url}><img src={process.env.PUBLIC_URL + '/pictures/' + charities.user_photo}
                                        width="500px" height="500px" /></a>
                                    <a class="btn btn-secondary btn-sm" href="#" role="button">View details &raquo;</a>
                                </div>
                            </td>
                        </div>
                    )}
                </div>
            </div>
        );

    }
}

class AddProjectComponent extends Component {


    constructor(props) {
        super(props);
        this.userData = JSON.parse(localStorage.getItem('user'));
        this.state = {
            userid: this.userData.userid,
            errors: {
                username: '',
                email: '',
                password: '',
                user_type: '',
                selectedFile: ''
            }
        }
        //this.saveName = this.saveName.bind(this);
    }


    //get recipientid
    async componentDidMount() {
        const responseDetails = await fetch('/api/recipients/pathbyuserid/' + this.state.userid);
        const responseRecipient = await responseDetails.json();
        this.setState({ recipient: responseRecipient, isLoading: false });
        console.log(responseRecipient)
    }

    validateUser = (e) => {
        e.preventDefault();
        if (validateForm(this.state.errors)) {
            console.log('Valid Form')
        } else {
            console.log('Invalid Input')
        }
    }


    handleFileChange = (e) => {
        console.log(e.target.files[0].name)
        this.setState(
            {
                selectedFile: e.target.files[0],
                loaded: 0,
            }
        )
        console.log(this.state.selectedFile)
    }


    addProject = (e) => {
        e.preventDefault();
        console.log(this.state)
        if (validateForm(this.state.errors)) {
            let project = {
                fk_recipient_id: this.state.recipient[0].pk_recipient_id,
                project_statement: this.state.project_statement,
                project_photo: this.state.project_photo,
                project_video: this.state.project_video,
                project_url: this.state.project_url

            };
            projectAdd(project).then(res => {
                console.log(res)
                console.log(this.state.userid)

            });
        } else {
            console.log('Invalid Input')
        }
    }

    savePhoto = () => {
        const data = new FormData()
        data.append('file', this.state.selectedFile)
        console.log(this.state.selectedFile)
        console.log(data)
        axios.post("http://localhost:8000/upload", data, { // receive two parameter endpoint url ,form data 
        })
            .then(res => { // then print response status
                console.log(res.statusText)
            })
    }


    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });

    // set user details to session storage
    componentWillUpdate(nextProps, nextState) {
        sessionStorage.setItem('user', JSON.stringify(nextState));
    }

    handleChange = (e) => {
        e.preventDefault();
        const { name, value } = e.target;
        console.log(name + value)
        let errors = this.state.errors;
        const { users, isLoading } = this.state;

        switch (name) {
            case 'username':
                errors.username = value.length < 5 ? 'Username must be at least 5 characters long!' : '';
                break;
            case 'email':
                errors.email =
                    validEmailRegex.test(value)
                        ? ''
                        : 'Email must be valid!';
                break;
            case 'password':
                errors.password =
                    value.length < 8
                        ? 'Password must be at least 8 characters long!'
                        : '';
                break;
            case 'user_type':
                errors.user_type =
                    value.length === 0
                        ? 'Please select a user type'
                        : '';
                break;
            default:
                break;
        }

        if (name === 'username') {
            {
                users.map(users => { (value === users.username) && (errors.username = 'Username already in use, please choose another') }
                )
