import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Avatar from "@material-ui/core/Avatar";
import GroupIcon from "@material-ui/icons/Group";
import { Link } from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import CircularProgress from "@material-ui/core/CircularProgress";
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap'

const useStyles = makeStyles(theme => ({
  table: {
    minWidth: 600
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main
  },
  paper: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    margin: `10px`,
    height: "100%",
    width: "99%",
    marginTop: theme.spacing(7)
  },
  link: {
    color: "rgba(0,0,0,0.65)",
    textDecoration: "none",
    marginLeft: "10%",
    alignSelf: "flex-start",
    "&:hover": {
      color: "rgba(0,0,0,1)"
    }
  }
}));



class Logon extends React.Component {

    constructor(props) {
        super(props);

      this.state = {
        currentPage: "Login",
        username: '',
        password: '',
        email: "",
        isLoading: true,
        hasLoginFailed: false,
        showSuccessMessage: false,
        users: []
    }


    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
}



handleChange(event) {
    this.setState(
        {
            [event.target.name]
                : event.target.value
        }
    )
}

loginClicked() {
    if(this.state.username==='in28minutes' && this.state.password==='dummy'){
        //AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
        this.setState({showSuccessMessage:true})
        this.setState({hasLoginFailed:false})
    }
    else {
         this.setState({showSuccessMessage:false})
         this.setState({hasLoginFailed:true})
    }
}

async componentDidMount() {
  /*const response = await fetch('/api/recipients/');
  const body = await response.json();
  const response1 = await fetch('/api/details/');
  const body1 = await response1.json();
  const response2 = await fetch('/api/name/');
  const body2 = await response2.json();
  this.setState({ recipients: body, details: body1, names: body2, isLoading: false }); */

  const response = await fetch('/api/users/');
  const body = await response.json();
  this.setState({ users: body, isLoading: false });
}



    render() {

      const { users, isLoading } = this.state;

      if (isLoading) {
          return <p>Loading...</p>;
      }
        return (

            <div>
                <h1>Login</h1>
                <div className="container">
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
                <div>{users.map(users => 
                            <TableRow key={users.pk_user_id}>
                              {!users.pk_user_id === null  ? <TableCell align="centre"></TableCell> : <TableCell align="centre">{users.pk_user_id}</TableCell>}
                              {!users.username === null ? <TableCell align="centre"></TableCell> : <TableCell align="centre">{users.username}</TableCell>}
                              {!users.email === null ? <TableCell align="centre"></TableCell> : <TableCell align="centre">{users.email}</TableCell>}
                              {!users.password === null ? <TableCell align="centre" ></TableCell> : <TableCell align="centre" >{users.password}</TableCell>}
                            </TableRow>
                          )}</div>
            </div>
            
        )
    }
}

export default Logon;
