class Login extends React.Component {

    
    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Login"
        };
      }




    render() {
        return (
            <>
                <TopNavBar data={this.state}/>
                <Logon data={this.state}/>
                
                <Footer />
                
            </>
        )
    }
}

ReactDOM.render(
    <Login />,
    document.getElementById('root')
);

export default Login;
