import TopNavBar from '../TopNavBar';
import React, { Component } from "react";
//import AddEmployee from "./Components/AddEmployee";
import { Route, BrowserRouter as Router, Redirect } from "react-router-dom";
//import Table from "./Components/Table";
//import App from './App';
//import background from './charitybackground.jfif';
import ReactDOM from 'react-dom';
import Recipients from "../Components/Recipients";
import HomePageCharities from '../Components/HomePageCharities';
import { Container } from 'react-bootstrap';
import Footer from '../Footer';
import Home from '../Home';
import Moment from 'moment';
import { format } from 'date-fns';

//import Registration from "../Components/Registration";

//import React, { useState } from 'react';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
//import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';
import CircularProgress from "@material-ui/core/CircularProgress";

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));


async function userAdd(toInput) {


    const response = await fetch("/api/user/insert", {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(toInput) // body data type must match "Content-Type" header
    });
    let newuser = await response.json();
    console.log(newuser.pk_user_id);
    let userid = newuser.pk_user_id;
    return userid;
}

async function detailsAdd(toInput) {
    const response = await fetch("/api/details/insert", {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json"
            // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        redirect: "follow", // manual, *follow, error
        referrerPolicy: "no-referrer", // no-referrer, *client
        body: JSON.stringify(toInput) // body data type must match "Content-Type" header
    });
    let newdetails = await response.json();
    let newdetailsid = newdetails.pk_details_id;
    return newdetailsid;
}


class AddUserComponent extends Component {


    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            email: '',
            firstName: '',
            lastName: '',
            orgname: '',
            message: null,
            userid: '',
            userstatement: null,
            user_type: null,
            duplicateusername: 0,
            duplicateemail: 0
        }
        this.saveUser = this.saveUser.bind(this);
    }

    setStateAsync(state) {
        return new Promise((resolve) => {
            this.setState(state, resolve)
        });
    }

    //get userlist
async componentDidMount() {

    const response = await fetch('/api/user/');
    const body = await response.json();
    this.setState({ users: body, isLoading: false });
  }

    saveUser = (e) => {
        e.preventDefault();
        console.log(this.state)
        const { users, isLoading } = this.state;
        {users.map(users =>
            {(this.state.username===users.username) && this.setState({duplicateusername:1})}     
        )}
        {users.map(users =>
            {(this.state.email===users.email) && this.setState({duplicateemail:1})}     
        )}

        let user = {
            username: this.state.username, password: this.state.password, email: this.state.email, reg_approval: "pending", user_type: this.state.user_type
        };
        {this.state.duplicateusername===0? console.log(this.state.duplicateusername ): console.log("nOT FOUND")}
        userAdd(user).then(res => {
            console.log(res)
            let resuser = res
            console.log(resuser)
            this.setState({ userid: res })
            console.log(this.state.userid)
            let details = { fk_user_id: this.state.userid, user_statement: this.state.userstatement }
            detailsAdd(details).then(res => {
                console.log(res)
            });
            console.log(res)
            console.log(this.state.user_added)
            this.setState({user_added: 1})
            console.log(this.state.duplicateusername)
            console.log(this.state.duplicateemail)
            //username: (users.username),
            this.setState({usertype: (res.user_type)})
        //usertype: (users.user_type)  
        
        });

    }

    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });

    // set user details to session storage
    componentWillUpdate(nextProps, nextState) {
        sessionStorage.setItem('user', JSON.stringify(nextState));
    }

    render() {

        return (
            <div className="container">
                <h2 className="text-center">Register as Donor or Charity</h2>
                <form>
                    <div className="form-group">
                        <label>User Name:</label>
                        <input type="text" placeholder="username" name="username" required className="form-control" value={this.state.username} onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <label>Password:</label>
                        <input type="password" placeholder="password" name="password" required className="form-control" value={this.state.password} onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <label>Email:</label>
                        <input type="email" placeholder="email" name="email" required className="form-control" value={this.state.email} onChange={this.onChange} />
                    </div>
                    <div className="form-group">
                        <label>User Statement:</label>
                        <input type="textarea" placeholder="User Statement" name="userstatement" className="form-control" value={this.state.userstatement} onChange={this.onChange} />
                    </div>
                    <div className="form-group">
                    <label>Charity &nbsp;</label>
                    <input type="radio" id="charity" name="user_type" value="2" onChange={this.onChange}/>
                    <label>&nbsp; Donor &nbsp;</label>
                    <input type="radio" id="donor" name="user_type" value="1" onChange={this.onChange}/>
                    </div>
                    {!this.state.user_added==1?<button className="btn btn-primary btn-success btn-block" onClick={this.saveUser}>Register</button>:
                    <div><h2>Registration has been received</h2>
                    <Nav.Link href="/login"><h3>Please login</h3></Nav.Link>
                    </div>}
                </form>
            </div>
        );
    }
}



class Register extends React.Component {





    constructor(props) {
        super(props);
        this.state = {
            currentPage: "Register"
        };
    }

    render() {
        return (
            <>
                <TopNavBar data={this.state} />
                <AddUserComponent data={this.state} />
                <Footer />
            </>
        )
    }
}

ReactDOM.render(
    <Register />,
    document.getElementById('root')
);

export default Register;
