import React, { useState } from 'react';
import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import GroupIcon from "@material-ui/icons/Group";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import { Navbar, Nav, NavDropdown, Form, FormControl} from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(7),
        display: "flex",
        flexDirection: "column",
        alignItems: "center"
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main
    },
    form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(3)
    },
    submit: {
        margin: theme.spacing(3, 0, 2)
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: "100%"
    }
}));

export default function Registration() {
    const classes = useStyles();
    const [firstLoad, setLoad] = React.useState(true);

    const [selectedDate, setSelectedDate] = React.useState(
      new Date("1998-04-02T21:11:54")
    );
    const [username, setUserName] = React.useState("");
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [user_type, selectedOption] = React.useState("");
    const [message, setMessage] = React.useState("");
    const [fk_user_id, setUserId] = React.useState("");
    const [user_statement, setStatement] = React.useState("");
    const [users, upDateUsers] = React.useState(this.state.users);
    const [details, upDateDetails] = React.useState([]);
    

    //const handleDateChange = date => setSelectedDate(date.target.value);
    const handleUserNameChange = event => setUserName(event.target.value);
    const handlEmailChange = event => setEmail(event.target.value);
    const handlePasswordChange = event => setPassword(event.target.value);
    const onChangeValue = event => selectedOption(event.target.value);
    const onStatementChange = event => setStatement(event.target.value);


      


    async function user(userInput) {
        const response = await fetch("/api/user/insert", {
            method: "POST", // *GET, POST, PUT, DELETE, etc.
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json"
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *client
            body: JSON.stringify(userInput) // body data type must match "Content-Type" header
        });
        let body =  response.json();
        upDateUsers(body);
        //let details =  response.json();
        //upDateDetails(details);
        //let fk_user_id = 26;
        //setUserId(details.pk_user_id);

    }

    async function details1(detailsInput) {
        const response = await fetch("/api/details/insert", {
            method: "POST", // *GET, POST, PUT, DELETE, etc.
            mode: "cors", // no-cors, *cors, same-origin
            cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
            credentials: "same-origin", // include, *same-origin, omit
            headers: {
                "Content-Type": "application/json"
                // 'Content-Type': 'application/x-www-form-urlencoded',
            },
            redirect: "follow", // manual, *follow, error
            referrerPolicy: "no-referrer", // no-referrer, *client
            body: JSON.stringify(detailsInput) // body data type must match "Content-Type" header
        });
        let body =  response.json();
        upDateDetails(details);
        //setMessage(body.pk_details_id ? body.pk_details_id : "Data update failed");
    }

    const handleSubmit = variables => {
        //{if (window.confirm('Ok to enter these details?')) this.onCancel("Return")}
        //const toInput = { userName, email, password, dob: selectedDate };
        const userInput = { username, email, password, user_type, reg_approval: "pending", reg_date: "2021-01-22" };
        user(userInput);
        let fk_user_id = user.pk_user_id ;
        const detailsInput = { fk_user_id , user_statement };
        details1(detailsInput);
        setUserName(users.email);
        setEmail(users.email);
        setPassword("");
        selectedOption("");
        setStatement(details.pk_user_id);
        setMessage(details.pk_user_id);
    };

    if (firstLoad) {
        // sampleFunc();
        setLoad(false);
        setMessage("Please hit save to register");
    }

    return (
        <Container component="main" maxWidth="xs">
            <CssBaseline />
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <GroupIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Register as Donor or Charity
        </Typography>
                <form className={classes.form} noValidate>
                    <Grid container spacing={2}>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="username"
                                value={username}
                                label="UserName"
                                userName="userName"
                                autoComplete="username"
                                onChange={handleUserNameChange}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                autoComplete="email"
                                name="email"
                                variant="outlined"
                                required
                                fullWidth
                                value={email}
                                id="email"
                                label="Email"
                                onChange={handlEmailChange}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                autoComplete="user_statement"
                                name="user_statement"
                                variant="outlined"
                                required
                                fullWidth
                                value={user_statement}
                                id="user_statement"
                                label="User Statement"
                                onChange={onStatementChange}
                            />
                        </Grid>
                        <Grid item xs={12}>
                            <TextField
                                variant="outlined"
                                required
                                fullWidth
                                id="password"
                                value={password}
                                label="Password"
                                name="password"
                                autoComplete="password"
                                onChange={handlePasswordChange}
                            />
                        </Grid>
                        <Grid  item xs ={6} height="10"> 
                            <Form.Group onChange={onChangeValue} controlId="user_type" height="50">
                                <Form.Label>Charity or Donor</Form.Label>
                                <Form.Control onChange={onChangeValue} as="select" multiple>
                                    <option value="2" id="user_type" >Charity</option>
                                    <option value="1" id="user_type" >Donor</option>
                                </Form.Control>
                            </Form.Group>
                        </Grid>
                    </Grid>
                    <Button
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        preventDefault
                        className={classes.submit}
                        onClick={handleSubmit}>
                        Register
                    </Button>

                    <Grid container justify="center">
            <Grid item>
              <Nav.Link to="/view">View Employee Records</Nav.Link>
            </Grid>
          </Grid>
                </form>
            </div>
            <div>{details.pk_details_id}</div>
            <Form>

  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
        </Container >
    );
}


/*
            <Grid item xs={12}>
              <TextField
                id="date"
                label="Date of birth"
                type="date"
                defaultValue="1998-04-02"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true
                }}
                onChange={handleDateChange}
              />
            </Grid>
*/
