import React, { Component } from "react";
import ReactDOM from 'react-dom';
import logo from '../acons3.png';
import Details from './Details';

class HomePageCharities extends React.Component {

    state = {
        isLoading: true,
        groups: []
    };

    async componentDidMount() {
        const response = await fetch('/api/recipients/');
        const body = await response.json();
        const response1 = await fetch('/api/details/');
        const body1 = await response1.json();
        this.setState({ recipients: body, details: body1, isLoading: false });
    }



    render() {
        const { recipients, details, isLoading } = this.state;

        if (isLoading) {
            return <p>Loading...</p>;
        }

        return (
            /*   <div className="App">
                   <header className="App-header">
                       <div className="App-intro">
                           <h2>Charity List</h2>
                           {recipients.map(recipients =>
                               <div key={recipients.pk_recipient_id}> {details.map(details =>
                                   <div key={details.pk_details_id}>
                                       <td>{details.fk_user_id === recipients.fk_user_id ? details.fk_user_id : null}</td>
                                   </div>
                               )}
                               </div>
                           )}
                       </div>
                   </header>
               </div> */
            <div class="container">
                <div class="row">
                    {recipients.map(recipients =>
                        <div key={recipients.pk_recipient_id}> {details.map(details =>
                            <div key={details.pk_details_id}>
                                <td>{details.fk_user_id === recipients.fk_user_id ?
                                    <div class="col-md-4">
                                        <h2>{details.user_statement}</h2>
                                        <a class="nav-item nav-link" href="https://donate.oxfamireland.org/euro/~my-donation?utm_source=brand&utm_medium=grants&utm_campaign=roi&ni=&gclid=EAIaIQobChMI29Sr7e_X7QIVC4BQBh10XgPeEAAYASAAEgLvQ_D_BwE"><img src={require('../pictures/oxfam.jfif')}
                                            class="rounded float-left  aristphoto" width="100%" height="100%" /></a>
                                        <a class="btn btn-secondary" href="#" role="button">View details &raquo;</a>
                                    </div>
                                    : null}</td>
                            </div>
                        )}
                        </div>
                    )}

                </div>
            </div>
        );

    }
}

export default HomePageCharities;
